import { useState, useEffect } from "react";
import { useForm, Controller } from "react-hook-form";
import {
    StyleSheet,
    Text,
    View,
    Picker,
} from "react-native";
import axios from "axios";
import { TextInput, Avatar, Button, IconButton } from "react-native-paper";
import {LoginStyles} from  "../assets/css/login_Styles";



const url = "http://192.168.10.12:4000/api";

export default function CreateAcc({navigation}) {
  
  
      const [username, setUsername] = useState('');
      const [name, setName] = useState('');
      const [password, setPassword] = useState('');
      const [reservedword, setReservedword] = useState('');

    /* Spinner de roles */
    const [role, setRole] = useState('');
    const roles = [
      {label: 'Admin', value: 'admin'},
      {label: 'User', value: 'user'},
    ];

    const onSave = async () => {
      try {
        // Validar los campos localmente si es necesario
  
        // Realizar la solicitud al servidor
        const response = await axios.post(url, {
          username,
          name,
          role,
          password,
          reservedword,
        });
  
        // Manejar la respuesta del servidor
        if (response.data.error) {
          setMessage('Hubo un error en el servidor.');
          setMessageStyle(LoginStyles.message_error);
        } else {
          setMessage('Usuario agregado correctamente.');
          setMessageStyle(LoginStyles.message_success);
          
        }
      } catch (error) {
        console.error('Error al procesar la solicitud', error);
        setMessage('Hubo un error al procesar la solicitud.');
        setMessageStyle(LoginStyles.message_error);
      }
    };




    

      

      
      return(
        <View style={LoginStyles.container}>
      <View style={LoginStyles.login_container}>
        <Text style={LoginStyles.title}>Registro de Usuario</Text>

        <View style={LoginStyles.input_container}>
          <Controller
            control={control}
            render={({ field }) => (
              <TextInput
                label="Nombre de Usuario"
                style={LoginStyles.input}
                onChangeText={(text) => field.onChange(text)}
                value={field.value}
              />
            )}
            name="username"
            rules={{ required: "El nombre de usuario es obligatorio" }}
          />
          {errors.username && (
            <Text style={LoginStyles.message_error}>
              {errors.username.message}
            </Text>
          )}

          <Controller
            control={control}
            render={({ field }) => (
              <TextInput
                label="Nombre"
                style={LoginStyles.input}
                onChangeText={(text) => field.onChange(text)}
                value={field.value}
              />
            )}
            name="name"
            rules={{ required: "El nombre es obligatorio" }}
          />
          {errors.name && (
            <Text style={LoginStyles.message_error}>{errors.name.message}</Text>
          )}

          <Picker
            selectedValue={selectedRole}
            onValueChange={(itemValue) => setSelectedRole(itemValue)}
          >
            <Picker.Item label="Usuario" value="usuario" />
            <Picker.Item label="Administrador" value="administrador" />
          </Picker>

          <Controller
            control={control}
            render={({ field }) => (
              <TextInput
                label="ContraseÃ±a"
                style={LoginStyles.input}
                onChangeText={(text) => field.onChange(text)}
                value={field.value}
                secureTextEntry={true}
              />
            )}
            name="password"
            rules={{ required: "La contraseÃ±a es obligatoria" }}
          />
          {errors.password && (
            <Text style={LoginStyles.message_error}>
              {errors.password.message}
            </Text>
          )}

          <Controller
            control={control}
            render={({ field }) => (
              <TextInput
                label="Palabra Reservada"
                style={LoginStyles.input}
                onChangeText={(text) => field.onChange(text)}
                value={field.value}
              />
            )}
            name="reservedword"
            rules={{ required: "La palabra reservada es obligatoria" }}
          />
          {errors.reservedword && (
            <Text style={LoginStyles.message_error}>
              {errors.reservedword.message}
            </Text>
          )}

          <Button style={LoginStyles.button} onPress={handleSubmit(onSubmit)}>
            Registrar
          </Button>
        </View>
      </View>
    </View>
      )

}
