import { useState, useEffect } from "react";
import { useForm, Controller } from "react-hook-form";
import {
    StyleSheet,
    Text,
    View,
    FlatList,
    TouchableOpacity,
    Pressable,
    Picker 
} from "react-native";
import axios from "axios";
import { TextInput, Avatar, Button, IconButton } from "react-native-paper";
import {LoginStyles} from  "../assets/css/login_Styles";


const url = "http://localhost:4000/api/forgotpassword"


export default function ForgotPass({navigation}) {






    const getCustomers = async () => {
        try {
          const response = await axios.get(url);
          console.log(response);
          if (!response.data.error) {
            setDataCustomers(response.data);
          }
          else {
            console.log("No hay clientes para mostrar");
          }
        } catch (error) {
          console.log(error);
        }
      };


    const forgotPasword = async (data) => {
        try {
          const response = await axios.post(`${url}`, data);
          setMessage("Cliente actualizado correctamente...");
          getCustomers();
          setIsError(false)
        } catch (error) {
          console.log(error);
        }
      };

}
