import React, { useState, useEffect } from 'react';
import { useForm, Controller } from 'react-hook-form';
import {
  StyleSheet,
  Text,
  View,
  FlatList,
  TouchableOpacity,
  Pressable,
  Picker,
} from 'react-native';
import { TextInput, Avatar, Button, IconButton } from 'react-native-paper';
import { useNavigation } from '@react-navigation/native';
import axios from 'axios';

const Care = () => {
  const [cars, setCars] = useState([]);
  const [selectedCar, setSelectedCar] = useState(null);
  const [formData, setFormData] = useState({
    platenumber: '',
    brand: '',
    status: 'disponible',
    dailyvalue: '',
  });

  const navigation = useNavigation();

  const fetchCars = async () => {
    try {
      const response = await axios.get('http://localhost:4000/api/cars/list');
      setCars(response.data.cars);
    } catch (error) {
      console.error('Error fetching cars:', error);
    }
  };

  const handleCreateCar = async () => {
    try {
      const response = await axios.post('http://localhost:4000/api/cars', formData);
      console.log(response.data.message);
      fetchCars(); // Actualiza la lista después de agregar un nuevo auto
    } catch (error) {
      console.error('Error creating car:', error);
    }
  };

  const handleUpdateCar = async () => {
    if (!selectedCar) return;

    try {
      const response = await axios.put(
        `http://localhost:4000/api/cars/${selectedCar.platenumber}`,
        formData
      );
      console.log(response.data.message);
      fetchCars(); // Actualiza la lista después de actualizar un auto
      setSelectedCar(null); // Reinicia el auto seleccionado
    } catch (error) {
      console.error('Error updating car:', error);
    }
  };

  const handleDeleteCar = async (platenumber) => {
    try {
      const response = await axios.delete(`http://localhost:4000/api/cars/${platenumber}`);
      console.log(response.data.message);
      fetchCars(); // Actualiza la lista después de eliminar un auto
    } catch (error) {
      console.error('Error deleting car:', error);
    }
  };

  useEffect(() => {
    fetchCars();
  }, []); // Se ejecuta solo una vez al montar el componente

  return (
    <View style={styles.container}>
      <Text>Car Management</Text>
      {/* Lista de autos */}
      <FlatList
        data={cars}
        keyExtractor={(item) => item.platenumber}
        renderItem={({ item }) => (
          <TouchableOpacity onPress={() => setSelectedCar(item)}>
            <Text>{item.brand}</Text>
          </TouchableOpacity>
        )}
      />
      {/* Formulario para agregar/editar autos */}
      <TextInput
        label="Plate Number"
        value={formData.platenumber}
        onChangeText={(text) => setFormData({ ...formData, platenumber: text })}
      />
      <TextInput
        label="Brand"
        value={formData.brand}
        onChangeText={(text) => setFormData({ ...formData, brand: text })}
      />
      <Picker
        selectedValue={formData.status}
        onValueChange={(itemValue) => setFormData({ ...formData, status: itemValue })}
      >
        <Picker.Item label="Disponible" value="disponible" />
        <Picker.Item label="No Disponible" value="no disponible" />
      </Picker>
      <TextInput
        label="Daily Value"
        value={formData.dailyvalue}
        onChangeText={(text) => setFormData({ ...formData, dailyvalue: text })}
      />
      {/* Botones de acción */}
      <Button onPress={handleCreateCar}>Add Car</Button>
      <Button onPress={handleUpdateCar}>Update Car</Button>
      {/* Botón para eliminar auto */}
      {selectedCar && (
        <Button onPress={() => handleDeleteCar(selectedCar.platenumber)}>Delete Car</Button>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
    justifyContent: 'center',
  },
});

export default CarScreen;
