import React from 'react';
import { View, Text, Button } from 'react-native';  // Add the missing import
import { useNavigation } from '@react-navigation/native';
import {IconButton } from "react-native-paper";
import { LoginStyles } from '../assets/css/login_Styles';

const AdminScreen = () => {
  const navigation = useNavigation();

  const goToCarScreen = () => {
    navigation.navigate('Car');
  };

  const goToReturnCarScreen = () => {
    navigation.navigate('returnCar');
  };

  return (
    <View style={LoginStyles.container}>
        <IconButton
                icon="arrow-left"
                color="#000"
                size={25}
                onPress={() => navigation.navigate('Login')} 
                style={LoginStyles.backButton}
            />
      <View style={LoginStyles.login_container}>
        <Text style={LoginStyles.title}>Menu Admin </Text>
        <View style={LoginStyles.button_container}>
                <Button
                style={LoginStyles.button}
                textColor="black"
                onPress={goToCarScreen}
                >
                    Iniciar Sesion
                </Button>
                <Button
                style={LoginStyles.button}
                textColor="black"
                onPress={() => navigation.navigate('CreateAcc')}
                >
                    ¡Crea una Cuenta!
                </Button>
                <Button
                    style={LoginStyles.button}
                    textColor="black"
                    onPress={() => navigation.navigate('ForgotPass')} 
                    >
                    ¿Olvido su constraseña?
                </Button>
            </View>
      </View>
    </View>
  );
};

export default AdminScreen;
