import { useState, useEffect } from "react";
import { useForm, Controller } from "react-hook-form";
import {
    StyleSheet,
    Text,
    View,
    FlatList,
    TouchableOpacity,
    Pressable,
    Picker,
} from "react-native";
import axios from "axios";
import { TextInput, Avatar, Button, IconButton } from "react-native-paper";
import {LoginStyles} from  "../assets/css/login_Styles";



const url = "http://192.168.10.12:4000/api";

export default function CreateAcc({navigation}) {

  const {
    control,
    handleSubmit,
    setError,
    formState: { errors },
  } = useForm();  
  
      const [username, setUsername] = useState('');
      const [name, setName] = useState('');
      const [password, setPassword] = useState('');
      const [reservedword, setReservedword] = useState('');

    /* Spinner de roles */
    const [role, setRole] = useState('');
    const roles = [
      {label: 'Admin', value: 'admin'},
      {label: 'User', value: 'user'},
    ];

    const onSave = async () => {
      try {
        // Validar los campos localmente si es necesario
  
        // Realizar la solicitud al servidor
        const response = await axios.post(url, {
          username,
          name,
          role,
          password,
          reservedword,
        });
  
        // Manejar la respuesta del servidor
        if (response.data.error) {
          setMessage('Hubo un error en el servidor.');
          setMessageStyle(LoginStyles.message_error);
        } else {
          setMessage('Usuario agregado correctamente.');
          setMessageStyle(LoginStyles.message_success);
          
        }
      } catch (error) {
        console.error('Error al procesar la solicitud', error);
        setMessage('Hubo un error al procesar la solicitud.');
        setMessageStyle(LoginStyles.message_error);
      }
    };




    

      

      
      return(
        <View style={LoginStyles.container}>
          <View style={LoginStyles.login_container}>

          <Text style={LoginStyles.title}>Crea tu cuenta </Text>

            <View style={LoginStyles.input_container}>

              <Controller
                control={control}
                rules={{
                required: true,
                }}
                render={({ field: { onChange, onBlur, value } }) => (
                <TextInput
                  label="Username"
                  style={LoginStyles.input}
                  onChangeText={setUsername}
                  value={username}
                  onBlur={onBlur}
                
                />
                
              )}
              name="username"
              />
              {errors.username?.type == "required" && (
              <Text style={{ color: "red" }}>Usuario es obligatorio</Text>
              )}

              <Controller
                control={control}
                rules={{
                required: true,
                }}
                render = {({field: { onChange, onBlur, value }}) => (
                <TextInput
                  label="Nombre"
                  style={LoginStyles.input}
                  onChangeText={name}
                  value={setName}
                  onBlur={onBlur}
                  
                />
                )}
              />
              {errors.name?.type == "required" && (
              <Text style={{ color: "red" }}>Nombres obligatorios</Text>
              )}

            <Controller
            control={control}

            render={({ field: {  onChange, onBlur, value } }) => (
            

            <TextInput
              label="Password" 
              style={[LoginStyles.input]}
              onChangeText={password}
              value={setPassword}
              onBlur={onBlur}
              secureTextEntry={!showPassword}
              icon={showPassword ? 'car-door' : 'car-door-lock'}
              onPress={() => setShowPassword(!showPassword)}
            />  
            )}
            name="password"
            />
            {errors.password?.type == "required" && (
            <Text style={{ color: "red" }}>Contraseña es obligatoria</Text>
            )}

            <Controller  
            control={control}
            render={({ field: { onChange, value } }) => (
            <Picker
            style={LoginStyles.input}
            selectedValue={value}
            onValueChange={itemValue => {
            onChange(itemValue)
            setRole(itemValue)  
            }}  
            >
            {roles.map(item => (
            <Picker.Item key={item.value} label={item.label} value={item.value} /> 
            ))}
            </Picker>
            )}
            name="role"
            />

            <Controller
              control={control}
              rules={{
              required: true,
            }}
            render={({ field: { onChange, onBlur, value } }) => (
            <TextInput
              label="Palabra Reservada"
              style={LoginStyles.input}
              onChangeText={reservedword}
              value={setReservedword}
              onBlur={onBlur}
              
            />
            )}
            name="reservword"
            />
            {errors.reservword?.type == "required" && (
            <Text style={{ color: "red" }}>Ingrese una palabra reservada</Text>
            )}

            <View style={LoginStyles.button_container}>
            <Button
            style={LoginStyles.button}
            textColor="black"
            onPress={handleSubmit(onSave)}
            >
            Crear cuenta
            </Button>
            <Button
            style={LoginStyles.button}
            textColor="black"
            onPress={() => navigation.navigate('Login')} 
            >
            Inicio de Sesión
            </Button>

              {message && (
                <Text style={messageStyle}>{message}</Text>
              )}

            </View>
            </View>
                  
          </View>

          
</View>
      )

}
