import { useState, useEffect } from "react";
import { useForm, Controller } from "react-hook-form";
import {
    StyleSheet,
    Text,
    View,
    FlatList,
    TouchableOpacity,
    Pressable,
    Picker 
} from "react-native";
import axios from "axios";
import { TextInput, Avatar, Button, IconButton } from "react-native-paper";
import {LoginStyles} from  "../assets/css/login_Styles";



const url = "http://172.16.60.103:3000/api/users";

export default function CreateAcc({navigation}) {
    const {
        control,
        handleSubmit,
        reset,
        setValue,
        formState: { errors },
      } = useForm({
        defaultValues: {
          username: "",
          name: "",
          password: "",
          role: "",
          reservword:""
        },
      });
      const [dataUsers, setDataUsers] = useState([]);
    const [id, setId] = useState("");
    const [message, setMessage] = useState("");
    const [isError, setIsError] = useState(false);
    const [showPassword, setShowPassword] = useState(false);

    /* Spinner de roles */
    const [role, setRole] = useState('');
    const roles = [
      {label: 'Admin', value: 'admin'},
      {label: 'User', value: 'user'},
    ];




    const getCustomers = async () => {
        try {
          const response = await axios.get(url);
          console.log(response);
          if (!response.data.error) {
            setDataUsers(response.data);
          }
          else {
            console.log("No hay usuarios para mostrar");
          }
        } catch (error) {
          console.log(error);
        }
      };

      const onSave = async (data) => {
        try {
          const resp = await axios.get(`${url}/byUsername/${data.username}`);
          console.log(data.username)
          if (resp.data == null) {
            const response = await axios.post(url, data);
            getCustomers();
            setMessage("Usuario agregado correctamente...");
            setIsError(false)
          }
          else {
            setMessage("Usuario ya EXISTE. Inténtelo con otro ...");
            setIsError(true);
          }
    
        } catch (error) {
          console.log(error);
        }
      };

      

      useEffect(()=>{
        getCustomers()
      },[])
      return(
        <View style={LoginStyles.container}>
          <View style={LoginStyles.login_container}>


          <Text style={LoginStyles.title}>LOGIN </Text>

<View style={LoginStyles.input_container}>

<Controller
  control={control}
  rules={{
   required: true,
}}
render={({ field: { onChange, onBlur, value } }) => (
<TextInput
  label="Username"
  style={LoginStyles.input}
  onChangeText={onChange}
  value={value}
  onBlur={onBlur}
  
/>
)}
name="username"
/>
{errors.username?.type == "required" && (
<Text style={{ color: "red" }}>Usuario es obligatorio</Text>
)}

<Controller
control={control}
rules={{
required: true,
}}
render = {({field: { onChange, onBlur, value }}) => (
<TextInput
  label="Nombre"
  style={LoginStyles.input}
  onChangeText={onChange}
  value={value}
  onBlur={onBlur}
  
/>
)}
name="name"
/>
{errors.name?.type == "required" && (
<Text style={{ color: "red" }}>Nombres obligatorios</Text>
)}

<Controller
control={control}

render={({ field: { onChange, onBlur, value } }) => (
<View style={LoginStyles.input_password}>

<TextInput
  label="Password" 
  style={[LoginStyles.input]}
  onChangeText={onChange}
  value={value}
  onBlur={onBlur}
  secureTextEntry={!showPassword}
  icon={showPassword ? 'car-door' : 'car-door-lock'}
  onPress={() => setShowPassword(!showPassword)}
/>  



</View>
)}
name="password"
/>
{errors.password?.type == "required" && (
<Text style={{ color: "red" }}>Contraseña es obligatoria</Text>
)}

<Controller  
control={control}
render={({ field: { onChange, value } }) => (
<Picker
style={LoginStyles.input}
selectedValue={value}
onValueChange={itemValue => {
onChange(itemValue)
setRole(itemValue)  
}}  
>
{roles.map(item => (
<Picker.Item key={item.value} label={item.label} value={item.value} /> 
))}
</Picker>
)}
name="role"
/>

<Controller
  control={control}
  rules={{
   required: true,
}}
render={({ field: { onChange, onBlur, value } }) => (
<TextInput
  label="Palabra Reservada"
  style={LoginStyles.input}
  onChangeText={onChange}
  value={value}
  onBlur={onBlur}
  
/>
)}
name="reservword"
/>
{errors.reservword?.type == "required" && (
<Text style={{ color: "red" }}>Ingrese una palabra reservada</Text>
)}

<View style={LoginStyles.button_container}>
<Button
style={LoginStyles.button}
textColor="black"
onPress={handleSubmit(onSave)}
>
Crear cuenta
</Button>
<Button
style={LoginStyles.button}
textColor="black"
onPress={() => navigation.navigate('Login')} 
>
Inicio de Sesión
</Button>

</View>
</View>
          </View>
                {/* Formulario para usuarios */}
          
</View>
      )

}
