import { useState, useEffect } from "react";
import { useForm, Controller } from "react-hook-form";
import {
    StyleSheet,
    Text,
    View,
    FlatList,
    TouchableOpacity,
} from "react-native";
import axios from "axios";
import { TextInput, Avatar, Button } from "react-native-paper";
import {LoginStyles} from  "../assets/css/login_Styles";



const url = "http://172.16.60.103:3000/api/users";

export default function CreateAcc({navigation}) {
    const {
        control,
        handleSubmit,
        reset,
        setValue,
        formState: { errors },
      } = useForm({
        defaultValues: {
          username: "",
          name: "",
          password: "",
          role: "",
          reservword:""
        },
      });
      const [dataUsers, setDataUsers] = useState([]);
    const [id, setId] = useState("");
    const [message, setMessage] = useState("");
    const [isError, setIsError] = useState(false);
    const [showPassword, setShowPassword] = useState(false);

    const getCustomers = async () => {
        try {
          const response = await axios.get(url);
          console.log(response);
          if (!response.data.error) {
            setDataUsers(response.data);
          }
          else {
            console.log("No hay usuarios para mostrar");
          }
        } catch (error) {
          console.log(error);
        }
      };

      const onSave = async (data) => {
        try {
          const resp = await axios.get(`${url}/byUsername/${data.username}`);
          console.log(data.username)
          if (resp.data == null) {
            const response = await axios.post(url, data);
            getCustomers();
            setMessage("Usuario agregado correctamente...");
            setIsError(false)
          }
          else {
            setMessage("Usuario ya EXISTE. Inténtelo con otro ...");
            setIsError(true);
          }
    
        } catch (error) {
          console.log(error);
        }
      };

      

      useEffect(()=>{
        getCustomers()
      },[])
      return(
        <View style={styles.all_container}>
                {/* Formulario para usuarios */}
        <Controller
            control={control}
            rules={{
             required: true,
        }}
        render={({ field: { onChange, onBlur, value } }) => (
          <TextInput
            label="Username"
            onBlur={onBlur}
            onChangeText={onChange}
            value={value}
            style={{ marginTop: 10 }}
            autoFocus
          />
        )}
        name="username"
      />
      {errors.username?.type == "required" && (
        <Text style={{ color: "red" }}>Usuario es obligatorio</Text>
      )}

      <Controller
        control={control}
        rules={{
          required: true,
        }}
        render = {({field: { onChange, onBlur, value }}) => (
          <TextInput
            label="Nombre"
            onBlur={onBlur}
            onChangeText={onChange}
            value={value}
            style={{ marginTop: 10 }}
          />
        )}
        name="name"
      />
      {errors.name?.type == "required" && (
        <Text style={{ color: "red" }}>Nombres obligatorios</Text>
      )}

<Controller
        control={control}
       
        render={({ field: { onChange, onBlur, value } }) => (
          <>
            <TextInput              
              onBlur={onBlur}
              onChangeText={onChange}
              value={value}
              secureTextEntry={!showPassword}
            />
            
            <Button
              onPress={() => setShowPassword(!showPassword)}
              title={showPassword ? 'Ocultar' : 'Mostrar'} 
            />
          </>
        )}
        name="password"
      />
        {errors.password?.type == "required" && (
          <Text style={{ color: "red" }}>Contraseña es obligatoria</Text>
        )}

      </View>
      )

}
